    setContador(contador + 1)
  }
  const restar = () => {
    setcontador(contador - 1)
  }

  return (
    <div className="App">
      <div>
        Calculadora
      </div>

      <button onClick={sumar}>+</button>
      <div>{contador}</div>
      <button>-</button>
      <div></div>
      <button>*</button>
      <div></div>
      <button>/</button>
      <div></div>
    </div>
  );
}

export default App;
