import React, { useState } from 'react';
import './App.css';

function App() {

  const [contador, setcontador ] = useState(0)

  const sumar = () => {
    setcontador(contador + 1)
  }

  const restar = () => {
    setcontador(contador - 1)
  }

  const multicplicar = () => {
    setcontador(contador * 1)
  }

  const dividir = () => {
    setcontador(contador / 1)
  }

  return (
    <div className="App">
      <div>
        Calculadora
      </div>

      <button onClick={sumar}>+</button>
      <div>{contador}</div>

      <button onClick={restar}>-</button>
      <div>{contador}</div>

      <button onClick={multicplicar}>*</button>
      <div>{contador}</div>

      <button onClick={dividir}>/</button>
      <div>{contador}</div>
    </div>
  );
}

export default App;
